package Java_Package;

public class ForLoopAndWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//This is a loop that starts from i = 10 and decrements i by 1 on each iteration, stopping when i < 1.
		for (int i=10;i>=1;i--)
			
		{
			System.out.println(i);
		}
		//Purpose: This is a loop that starts from i = 1 and increament i by 1 on each iteration, stopping when i=10.
for (int i=1;i<=10;i++)
	
{
	System.out.println(i);
}

int number = 1; //initialize the controle variable
//"while" loop to print number from 1 to 5
while (number <=5)
{
	System.out.println("number"+number);
	number ++; // increase numer by 1 after each iteration
}

	}
	

}
