package Java_Package;

public class Relational_Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//equal to (==)
		//not equal to (!=)
		//greater than=(>)
		//less than = (<)
		//greater than or equal to =(>=)
		//less than or equal to = (<+)
		
		int a = 20;
		int b = 40;
		
		System.out.println(a>b);
		System.out.println(a<b);
		System.out.println(a>=b);
		System.out.println(a<=b);
		System.out.println(a==b);
		System.out.println(a!=b);
		
	}

}
