package Java_Package;

public class java_key_words {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num= 10;
		double num1 = 15.789;
		float num3 = (float) 10.20;
		char c= 's';
		boolean value = true;
		String name= "sunita2023";				
		
		System.out.println("the integer value "+num);
		System.out.println("the double value "+num1);
		System.out.println("the float value "+num3);
		System.out.println("the character value "+c);
		System.out.println("the boolean value "+value);
		System.out.println("the name of person "+name);

	}

}
